mapper.update:
UPDATE tbRsv
SET
	rsvTotalPrice=#{totalPrice}
WHERE 
	rsvCode=#{rsvCode}

mapper.insert:

	INSERT INTO tbRsvDetail
		(rsvDetailItem
		, rsvDetailNum
		, rsvDetailOnePrice
		, rsvDetailTotalPrice)
	VALUES 
		(#{itemCode}
		, #{itemCount}
		, #{itemRentalPrice}
		, #{itemTotalPrice})

INSERT INTO tbRsv(
	  rsvUserId
	, rsvUserName
	, rsvUserPhone
	, rsvUserEmail
	, rsvUserRequest
	, rsvTotalPrice
	, rsvStartDateTime
	, rsvEndDateTime
	, rsvRegDate
)VALUES(
	  #{rsvUserId}
	, #{rsvUserName}
	, #{rsvUserPhone}
	, #{rsvUserEmail}
	, #{rsvUserRequest}
	, #{rsvTotalPrice}
	, #{rsvStartDateTime}
	, #{rsvEndDateTime}
	, NOW()
);
